'use strict';
var scroll = {
        position: 0,
        scrollOff: function () {
            (scroll.position = window.scrollY),
                (document.body.style.cssText = '\n            overflow: hidden;\n            padding-right: '
                    .concat(
                        window.innerWidth - document.body.offsetWidth,
                        'px;\n            position: fixed;\n            top: -',
                    )
                    .concat(
                        scroll.position,
                        'px;\n            left: 0;\n            height: 100vh;\n            width: 100vw;\n            ',
                    )),
                (document.documentElement.style.scrollBehavior = 'unset');
        },
        scrollOn: function () {
            (document.body.style.cssText = ''),
                window.scrollTo({ top: scroll.position }),
                (document.documentElement.style.scrollBehavior = '');
        },
    },
    burger = function () {
        var e = document.getElementById('main'),
            t = document.getElementById('burger'),
            n = document.getElementById('menu'),
            o = document.querySelectorAll('.nav__item'),
            c = function () {
                var t = n.classList.contains('menu-active');
                window.matchMedia('(max-width: 768px)').matches &&
                    (t
                        ? (n.classList.remove('menu-active'), e.classList.remove('main-menu-active'), scroll.scrollOn())
                        : (n.classList.add('menu-active'), e.classList.add('main-menu-active'), scroll.scrollOff()));
            };
        t.addEventListener('click', c),
            o.forEach(function (e) {
                return e.addEventListener('click', c);
            });
    },
    slider = function () {
        var e,
            t = document.querySelector('.slider__inner'),
            n = document.querySelectorAll('.card'),
            o = n.length,
            c = 0,
            i = 0,
            r = n[0].offsetWidth,
            s = Number(getComputedStyle(t).columnGap.replace('px', '')),
            l = window.matchMedia('(max-width: 1050px)').matches,
            d = r + s,
            a = d * (l ? o - 1 : o - 3),
            u = function () {
                (i += 10) >= 4e3 &&
                    ((c += d) > a && (c = 0), (t.style.transform = 'translateX(-'.concat(c, 'px)')), (i = 0));
            },
            m = function () {
                return (e = setInterval(u, 10));
            },
            v = function () {
                return clearInterval(e);
            };
        t.addEventListener('mouseover', v),
            t.addEventListener('mouseout', m),
            window.addEventListener('resize', function () {
                (t.style.transform = 'translateX(-0px)'), v(), m();
            }),
            m();
    };
burger(), slider();
